def name_init(full_name):
    words = full_name.split()
    initials = ''.join(word[0].upper() for word in words)
    return initials

class Animator_dedicated:
    def __init__(self, vlz, save2PIL=True):
        self.vlz = vlz
        self.alpha_his = 0.15
        self.alpha_now = 1
        self.histories = None
        self.label_off = None
        self.label_ball = None
        self.predict_off = None
        self.predict_ball = None
        self.show_name = True
        self.save2PIL = save2PIL

    def process_data(self, data):
        if len(data) == 1:
            data = data[0]
        vals = np.array(data)  # (T, N, 2)

        # 对每个点的轨迹在时间轴上进行平滑，保持起始和终止点不变
        def smooth_trajectory(vals, window=3):
            T, N, D = vals.shape
            smoothed = vals.copy()
            for n in range(N):
                for d in range(D):
                    # 起始和终止点不变，中间的点做平滑
                    for t in range(1, T-1):
                        left = max(0, t - window//2)
                        right = min(T, t + window//2 + 1)
                        smoothed[t, n, d] = np.mean(vals[left:right, n, d])
                    # 起始和终止点保持原样
                    smoothed[0, n, d] = vals[0, n, d]
                    smoothed[T-1, n, d] = vals[T-1, n, d]
            return smoothed

        vals = fit_cs_curve_ar(vals)  # (T_密集, N, 2)
        vals = smooth_trajectory(vals, window=3)
        vals[:,:,0]+= 52.5  # x
        vals[:,:,1]+= 34  # y
        return vals

    def plot_data(self, vals, alpha=1, is_gt=True, mask_defend=False, mask_unrelevant=False, draw_magnifier=False, no_nodes=False):
        if draw_magnifier:
            relevant_players = self._get_relevant_players(vals, threshold=20)
            self._get_magnifier_window(vals, relevant_players, margin=1)

        # Curves
        self.vlz.plot_curves(vals[:,0:1,:], part='ball' if is_gt else 'ball_pred', alpha=alpha, zorder=10)
        if not mask_unrelevant:
            self.vlz.plot_curves(vals[:, 1:12, :], part='off' if is_gt else 'off_pred', alpha=alpha, zorder=9)
        else:
            self.vlz.plot_curves(vals[:, self._get_relevant_players(vals, threshold=20),:], part='off' if is_gt else 'off_pred', alpha=alpha, zorder=9)
        if not mask_defend:
            self.vlz.plot_curves(vals[:, 12:23, :], part='def' if is_gt else 'def_pred', alpha=alpha)

        # Nodes
        if not no_nodes:
            self.vlz.plot_nodes(x=vals[-1, 0:1, 0], y=vals[-1, 0:1, 1], part='ball' if is_gt else 'ball_pred', alpha=alpha, zorder=10)
            if not mask_unrelevant:
                self.vlz.plot_nodes(x=vals[-1, 1:12, 0], y=vals[-1, 1:12, 1], part='off' if is_gt else 'off_pred', alpha=alpha, zorder=9)
            else:
                self.vlz.plot_nodes(x=vals[-1, self._get_relevant_players(vals, threshold=20), 0], y=vals[-1, self._get_relevant_players(vals, threshold=20), 1], part='off' if is_gt else 'off_pred', alpha=alpha, zorder=9)
            if not mask_defend:
                self.vlz.plot_nodes(x=vals[-1, 12:23, 0], y=vals[-1, 12:23, 1], part='def' if is_gt else 'def_pred', alpha=alpha)


    def animate_x(self, x, path='try.jpg'):
        self.vlz.__init__()
        self.plot_data(self.process_data(x), self.alpha_now)
        if not self.save2PIL:
            self.vlz.save(path)
        else:
            return self.vlz.save_to_PILImage()

    def animate_label(self, x, label, mask_defend=False, path='try.jpg'):
        self.vlz.__init__()
        self.plot_data(self.process_data(x), self.alpha_his)
        self.plot_data(self.process_data(label), self.alpha_now, mask_defend=False)
        if not self.save2PIL:
            self.vlz.save(path)
        else:
            return self.vlz.save_to_PILImage()

    def animate_predict(self, x, prediction, mask_defend=False, mask_unrelevant=False, draw_magnifier=False, path='try.jpg'):
        # 初始化球场
        self.vlz.__init__()
        self.plot_data(self.process_data(x), self.alpha_his)
        self.plot_data(self.process_data(prediction), self.alpha_now, is_gt=False, mask_defend=mask_defend, mask_unrelevant=mask_unrelevant, draw_magnifier=draw_magnifier)
        # self.vlz.show()
        if not self.save2PIL:
            self.vlz.save(path)
        else:
            return self.vlz.save_to_PILImage()

    def animate_predict_forQ(self, x, prediction, mask_defend=False, mask_unrelevant=False, draw_magnifier=False, path='try.jpg', name_list=None, role_list=None):
        # 初始化球场
        self.vlz.__init__()
        x, prediction = np.array(x), np.array(prediction)
        self.plot_data(self.process_data(x[-3:, :, :]), self.alpha_his, no_nodes=True)
        his_pre = np.concatenate((x[-1:, :, :], prediction), axis=0)
        self.plot_data(self.process_data(his_pre), self.alpha_now, is_gt=True, mask_defend=mask_defend, mask_unrelevant=mask_unrelevant, draw_magnifier=draw_magnifier)
        if role_list is not None:
            self.vlz.plot_names(self.process_data(his_pre)[-1, 1:12, 0], self.process_data(his_pre)[-1, 1:12, 1], name_list[1:12], role_list[1:12], initials=True)
        # self.vlz.show()
        if not self.save2PIL:
            self.vlz.save(path)
        else:
            return self.vlz.save_to_PILImage()

    def animate_predict_forQ_defenseLabel(self, x, prediction, path='try.jpg'):
        self.vlz.__init__()
        x, prediction = np.array(x), np.array(prediction)
        self.plot_data(self.process_data(x[:, -16:, :, :]), self.alpha_his, no_nodes=True)  # part of history
        his_pre = np.concatenate((x[:, -1:, :, :], prediction), axis=1)
        self.plot_data(self.process_data(his_pre), self.alpha_now, is_gt=True, mask_defend=False, mask_unrelevant=False, draw_magnifier=False)
        # self.vlz.show()
        if self.save2PIL:
            return self.vlz.save_to_PILImage()
        else:
            self.vlz.save(path)

    def animate_predict_and_label(self, x, prediction, label, path='try.jpg'):
        self.vlz.__init__()
        self.plot_data(self.process_data(x), self.alpha_his)
        self.plot_data(self.process_data(label), self.alpha_now, is_gt=True)
        self.plot_data(self.process_data(prediction), self.alpha_now, is_his=False)
        if not self.save2PIL:
            self.vlz.save(path)
        else:
            return self.vlz.save_to_PILImage()

    def video_x(self, x, path='try.mp4'):
        frames = []
        x_np = np.array(x)
        x_np = self.process_data(x_np)
        T = x_np.shape[0]
        for t in range(1, T):
            self.vlz.__init__()
            self.plot_data(x_np[:t+1, :, :], self.alpha_now)
            img = self.vlz.save_to_PILImage()
            frames.append(np.array(img))
        imageio.mimsave(path, frames, fps=10, format='ffmpeg', macro_block_size=None)

    def video_predict_forQ_defenseLabel(self, x, prediction, label, path='try.mp4'):
        frames = []
        x = np.array(x)
        prediction = np.array(prediction)
        label = np.array(label)
        prediction_dL = np.concatenate((prediction[:, :, :12, :], label[:, :, 12:, :]), axis=2)  # 防守球员换为label的
        prediction = np.concatenate((x[:, -1:, :, :], prediction_dL), axis=1)

        x = self.process_data(x)
        prediction = self.process_data(prediction)
        label = self.process_data(label)
        T = prediction.shape[0]
        for t in range(T):
            self.vlz.__init__()
            self.plot_data(x[-30:, :, :], self.alpha_his, no_nodes=True)  # history last 3 second
            self.plot_data(prediction[:t+1, :, :])
            img = self.vlz.save_to_PILImage()
            frames.append(np.array(img))
        imageio.mimsave(path, frames, fps=10, format='ffmpeg', macro_block_size=None)

    def _get_relevant_players(self, data, threshold=20):
        """
        返回所有时刻中距离球小于threshold的球员索引集合。
        data: (T, N, 2) ndarray, 0号为球，1-11为off, 12-22为def
        返回: 相关球员的索引list（包括球本身0）
        """
        vals = np.array(data)  # (T, N, 2)
        ball_traj = vals[:, 0, :]  # (T, 2)
        relevant = set([])
        for n in range(1, 12):  # attack players
            dists = np.linalg.norm(vals[:, n, :] - ball_traj, axis=1)
            if np.any(dists < threshold):
                relevant.add(n)
        return sorted(list(relevant))

    def _get_magnifier_window(self, vals, relevant_idx, margin=1, scale=1.2):
        """
        计算放大镜窗口，覆盖球轨迹和所有相关球员轨迹，矩形区域，以所有相关点的边界为基础，margin为边界外扩像素，scale为放大比例。
        返回(left, right, bottom, top)
        """
        relevant_traj = vals[:, relevant_idx, :].reshape(-1, 2)
        min_x, min_y = relevant_traj.min(axis=0)
        max_x, max_y = relevant_traj.max(axis=0)
        center_x = (min_x + max_x) / 2
        center_y = (min_y + max_y) / 2
        width = (max_x - min_x) * scale + margin * 2
        height = (max_y - min_y) * scale + margin * 2
        left = center_x - width / 2
        right = center_x + width / 2
        bottom = center_y - height / 2
        top = center_y + height / 2
        self.vlz.draw_magnifier(left, right, bottom, top)




